import React, { useState } from 'react';
import axios from 'axios';
const API = axios.create({ baseURL: process.env.REACT_APP_API || 'http://localhost:4000/api' });
export default function QueueStatus(){ const [ticket,setTicket]=useState(''); const [status,setStatus]=useState(null);
  const check = async ()=>{ try{ const res=await API.get(`/queue/status/${ticket}`); setStatus(res.data); }catch(e){ setStatus({error:'not found'});} };
  return (<div><h2>Check Status</h2><input value={ticket} onChange={e=>setTicket(e.target.value)} placeholder='TICKET-ID'/> <button onClick={check}>Check</button>{status && <pre>{JSON.stringify(status,null,2)}</pre>}</div>); }
